import React, { useEffect, useState } from 'react';
import UserItem from './UserItem/UserItem';

interface User {
    id: number;
    firstName: string;
    lastName: string;
    age: number;
    position?: { x: number; y: number };
}

const UserList: React.FC = () => {
    const [users, setUsers] = useState<User[]>([]);

    useEffect(() => {
        fetch('https://dummyjson.com/users')
            .then(res => res.json())
            .then(data => {
                const initialUsers = data.users.map((user: any) => ({
                    ...user,
                    position: { x: 0, y: 0 }  // Початкове значення для позиції
                }));
                setUsers(initialUsers);
            })
            .catch(error => console.error('Помилка:', error));
    }, []);

    // Функція для оновлення позиції елементів
    const handleDragStop = (index: number, e: any, data: { x: number; y: number }) => {
        const newUsers = [...users];
        newUsers[index] = {
            ...newUsers[index],
            position: { x: data.x, y: data.y }
        };
        setUsers(newUsers);
    };

    return (
        <div className="flex flex-col justify-center w-[500px] border border-black mx-auto rounded-lg">
            <h1 className='py-7 text-3xl text-center'>Users</h1>
            <ul>
                {users.map((user, index) => (
                    <li key={user.id}>
                        <UserItem
                            id={user.id}
                            userName={user.firstName}
                            userLastName={user.lastName}
                            age={user.age}
                            position={user.position}
                            onDragStop={(e, data) => handleDragStop(index, e, data)}  // Передаємо обробник
                        />
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default UserList;
