import React, { useEffect, useState } from 'react';
import Draggable from 'react-draggable';
import UserItem from './UserItem/UserItem';

interface User {
    id: number;
    firstName: string;
    lastName: string;
    age: number;
}

const UserList: React.FC = () => {
    const [users, setUsers] = useState<User[]>([]);
    const itemHeight = 80; // Висота одного елемента списку (в пікселях)

    useEffect(() => {
        fetch('https://dummyjson.com/users')
            .then(res => res.json())
            .then(data => setUsers(data.users as User[]))
            .catch(error => console.error('Помилка:', error));
    }, []);

    const handleDrag = (draggedUserId: number, newIndex: number) => {
        // Знайти індекс перетягуваного елемента
        const currentIndex = users.findIndex(user => user.id === draggedUserId);

        if (currentIndex !== -1 && newIndex !== currentIndex) {
            // Копіюємо список для оновлення
            const updatedUsers = [...users];
            
            // Видаляємо перетягуваний елемент
            const [draggedUser] = updatedUsers.splice(currentIndex, 1);
            
            // Вставляємо його на нову позицію
            updatedUsers.splice(newIndex, 0, draggedUser);
            
            // Оновлення ID для правильного порядку
            const reorderedUsers = updatedUsers.map((user, index) => ({
                ...user,
                id: index + 1 // Присвоєння нового ID відповідно до позиції
            }));

            setUsers(reorderedUsers);
        }
    };

    return (
        <div className="flex flex-col justify-center w-[500px] border border-black mx-auto rounded-lg">
            <h1 className='py-7 text-3xl text-center'>Users</h1>
            <ul>
                {users.map((user, index) => (
                    <Draggable
                        key={user.id}
                        axis="y" // Переміщення тільки по осі Y
                        bounds="parent"
                        onStop={(e, data) => {
                            // Обчислення нового індексу
                            const newIndex = Math.min(
                                Math.max(0, Math.round(data.y / itemHeight)),
                                users.length - 1
                            );
                            handleDrag(user.id, newIndex);
                        }}
                    >
                        <li style={{ height: `${itemHeight}px` }}>
                            <UserItem
                                id={user.id}
                                userName={user.firstName}
                                userLastName={user.lastName}
                                age={user.age}
                            />
                        </li>
                    </Draggable>
                ))}
            </ul>
        </div>
    );
};

export default UserList;
