import React, { useEffect, useState } from 'react';
import Draggable from 'react-draggable';
import UserItem from './UserItem/UserItem';

interface User {
    id: number;
    firstName: string;
    lastName: string;
    age: number;
}

const UserList: React.FC = () => {
    const [users, setUsers] = useState<User[]>([]);
    const itemHeight = 90; // Висота одного елемента списку (в пікселях)

    useEffect(() => {
        fetch('https://dummyjson.com/users')
            .then(res => res.json())
            .then(data => setUsers(data.users as User[]))
            .catch(error => console.error('Помилка:', error));
    }, []);

    const handleDrag = (userId: number, newIndex: number) => {
      
        const currentIndex = users.findIndex(user => user.id === userId);

        if (currentIndex !== -1 && newIndex !== currentIndex) {
            // Змінити порядок елементів у списку
            const updatedUsers = [...users];
            const [movedUser] = updatedUsers.splice(currentIndex, 1);
            updatedUsers.splice(newIndex, 0, movedUser);

            setUsers(updatedUsers);
        }
    };

    return (
        <div className="flex flex-col justify-center w-[500px] border border-black mx-auto rounded-lg">
            <h1 className='py-7 text-3xl text-center'>Users</h1>
            <ul>
                {users.map((user, index) => (
                    <Draggable
                        key={index+1}
                        axis="y" 
                        bounds="parent"
                        onStop={(e, data) => {
                            
                            const newIndex = Math.min(
                                Math.max(0, Math.round(data.y / itemHeight)),
                                users.length - 1
                            );
                            handleDrag(user.id, newIndex);
                        }}
                    >
                        <li >
                            <UserItem
                                id={index+1}
                                userName={user.firstName}
                                userLastName={user.lastName}
                                age={user.age}
                            />
                        </li>
                    </Draggable>
                ))}
            </ul>
        </div>
    );
};

export default UserList;
