import React, { useEffect, useState } from 'react';
import UserItem from './UserItem/UserItem';

interface User {
    id: number;
    firstName: string;
    lastName: string;
    age: number;
    position?: { x: number; y: number };
}

const UserList: React.FC = () => {
    const [users, setUsers] = useState<User[]>([]);

    useEffect(() => {
        fetch('https://dummyjson.com/users')
            .then(res => res.json())
            .then(data => setUsers(data.users as User[]))
            .catch(error => console.error('Помилка:', error));
    }, []);


    return (
        <div className="flex flex-col justify-center w-[500px] border border-black mx-auto rounded-lg p-5">
            <h1 className='py-7 text-3xl text-center'>Users</h1>
            <ul>
                {users.map((user) => (
                    <li key={user.id}>
                        {/* <UserItem
                        
                            onDragStart={(e) => { dragStartHandle(e) }}
                            id={user.id}
                            userName={user.firstName}
                            userLastName={user.lastName}
                            age={user.age}
                        /> */}
                        <div draggable={true}
                            onDragStart={(e) => {dragStartHandler(e,user) }}
                            onDragLeave={(e) => {dragLeaveHandler(e) } }
                            onDragEnd={(e) => {dragEndHandler(e) } }
                            onDragOver={(e) => {dragOverHandler(e) } }
                            onDrop={ (e) => {dropHandler(e) }}
                            className="flex items-center justify-between py-7 px-10 text-xl border rounded-lg mb-4">
                            <div>{user.id}</div>
                            <div>{user.firstName} {user.lastName}</div>
                            <div>{user.age} years</div>
                        </div>
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default UserList;
