import React, { useEffect, useState } from 'react';

interface User {
    id: number;
    firstName: string;
    lastName: string;
    age: number;
}

const UserList: React.FC = () => {
    const [users, setUsers] = useState<User[]>([]);
    const [currentUser, setCurrentUser] = useState<User | null>(null);

    useEffect(() => {
        fetch('https://dummyjson.com/users')
            .then(res => res.json())
            .then(data => setUsers(data.users as User[]))
            .catch(error => console.error('Помилка:', error));
    }, []);

    const dragStartHandler = (e: React.DragEvent<HTMLDivElement>, user: User) => {
        setCurrentUser(user);
    };

    const dragOverHandler = (e: React.DragEvent<HTMLDivElement>) => {
        e.preventDefault(); // Потрібно для того, щоб дозволити drop
    };

    const dropHandler = (e: React.DragEvent<HTMLDivElement>, targetUser: User) => {
        e.preventDefault();

        if (currentUser) {
            // Отримуємо оновлені користувачі зі зміною позицій
            const updatedUsers = users.map((user) => {
                if (user.id === targetUser.id) {
                    // Замінюємо цільовий користувач на того, кого перетягують
                    return { ...user, id: currentUser.id };
                } else if (user.id === currentUser.id) {
                    // Перетягнутий користувач отримує ID цільового
                    return { ...user, id: targetUser.id };
                }
                return user;
            });

            // Оновлюємо стан користувачів
            setUsers(updatedUsers);
        }
    };

    const sortUsers = (a: User, b: User) => {
        return a.id - b.id; // Сортуємо за зростанням ID
    };

    return (
        <div className="flex flex-col justify-center w-[500px] border border-black mx-auto rounded-lg p-5">
            <h1 className="py-7 text-3xl text-center">Users</h1>
            <ul>
                {users.sort(sortUsers).map((user) => (
                    <li key={user.id}>
                        <div
                            draggable={true}
                            onDragStart={(e) => dragStartHandler(e, user)}
                            onDragOver={(e) => dragOverHandler(e)}
                            onDrop={(e) => dropHandler(e, user)}
                            className="flex items-center justify-between py-7 px-10 text-xl border rounded-lg mb-4 bg-gray-100 cursor-pointer"
                        >
                            <div>{user.id}</div>
                            <div>{user.firstName} {user.lastName}</div>
                            <div>{user.age} years</div>
                        </div>
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default UserList;
